import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Task } from '../../../models/task.model';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.css'],
})
export class TaskComponent {
  @Input() task: Task;

  // tslint:disable-next-line: no-output-on-prefix
  @Output()
  onPinTask = new EventEmitter<string>();

  // tslint:disable-next-line: no-output-on-prefix
  @Output()
  onArchiveTask = new EventEmitter<string>();

  onPin(id: string) {
    this.onPinTask.emit(id);
  }

  onArchive(id: string) {
    this.onArchiveTask.emit(id);
  }
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Task } from '../../../models/task.model';

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.css'],
})
export class TaskListComponent {
  tasksInOrder: Task[] = [];

  @Input() loading = false;

  @Input()
  set tasks(arr: Task[]) {
    this.tasksInOrder = [
      ...arr.filter((t) => t.state === 'TASK_PINNED'),
      ...arr.filter((t) => t.state === 'TASK_INBOX'),
    ];
  }

  // tslint:disable-next-line: no-output-on-prefix
  @Output()
  onPinTask = new EventEmitter<string>();

  // tslint:disable-next-line: no-output-on-prefix
  @Output()
  onArchiveTask = new EventEmitter<string>();
}

import { Component } from '@angular/core';
import { Select, Store } from '@ngxs/store';
import { Observable } from 'rxjs';
import { Task } from '../../../models/task.model';
import { ArchiveTask, PinTask, TaskState } from '../../../state/task.state';

@Component({
  selector: 'app-task-list-wrapper',
  templateUrl: './task-list-wrapper.component.html',
  styleUrls: ['./task-list-wrapper.component.css'],
})
export class TaskListWrapperComponent {
  @Select(TaskState.tasks) tasks$: Observable<Task[]>;

  constructor(private store: Store) {}

  archiveTask(id: string) {
    this.store.dispatch(new ArchiveTask(id));
  }

  pinTask(id: string) {
    this.store.dispatch(new PinTask(id));
  }
}

